    public void setDescendantFocusability(int focusability) {
            switch (focusability) {
                case FOCUS_BEFORE_DESCENDANTS:
                case FOCUS_AFTER_DESCENDANTS:
                case FOCUS_BLOCK_DESCENDANTS:
                    break;
