        private class TextImageAdapter extends BaseAdapter{
            private Context mContext;
        	public TextImageAdapter(Context context) {
    			this.mContext=context;
    		}
            /**
             * 
             */
    		public int getCount() {
    			return texts.length;
    		}

    		public Object getItem(int position) {
    			return null;
    		}

